mapper.insert:
SET @var_id := (SELECT MAX(id_tourament) from tournament_tbl);
	INSERT INTO history_tbl(
	id_team,
	id_tour,
	id_member
	) VALUES(
	${idTeam},
	@var_id,
	${idMember}
	)

mapper.select:
SELECT * FROM history_tbl Where id_member = ${idMember} and id_team = ${idTeam}
ORDER BY id_member DESC
LIMIT 1;
