mapper.select:
	 Select * FROM schedule_tbl inner join tournament_tbl on schedule_tbl.schedule_tour=tournament_tbl.id_tourament  
 inner join team_tbl on schedule_tbl.schedule_team1=team_tbl.id_team 
 or schedule_tbl.schedule_team2=team_tbl.id_team where id_schedule=${idSchedule}

mapper.update:
Update schedule_tbl set
schedule_score1=#{scoreTeam1} ,
schedule_title=#{title}
schedule_score2=#{scoreTeam2},
schedule_video=#{video},
schedule_winner=#{idwinner},
schedule_description=#{description}
Where id_schedule=#{idSchedule};

